import React from 'react';
import axios from 'axios';
import './Home.css';

const CenteredContainer = ({ children }) => {
    return (
      <div className="centered-container">
        {children}
      </div>
    );
  }
const Home = () => {
  const [tweet, setTweet] = React.useState('');
  //const [image, setImage] = React.useState(null);

  const handleTweetChange = (e) => {
    setTweet(e.target.value);
  };

  // const handleImageChange = (e) => {
  //   setImage(e.target.files[0]);
  // };



  
  const handleSubmit = async (e) => {
    e.preventDefault();
  
    const tweetData = {
      tweet:tweet
     
    };

    axios.defaults.headers.post['Content-Type'] = 'application/json';

    axios.post("http://localhost:5000/home", tweetData)
      .then(response => {
        console.log(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

return (
  <div className='Home'>
    <h1>Welcome to the Home Page</h1>
    <CenteredContainer className='centered-container'>
      <div>
        <h2>Post a Tweet</h2>
        <form onSubmit={handleSubmit}>
          <div>
            <textarea className='tweettext' 
              id="tweet"
              value={tweet}
              onChange={handleTweetChange}
              placeholder="What's happening?"
              rows={5}
              cols={100}
            />
          </div>
          <button className='button' type="submit">Tweet</button>
        </form>
      </div>

      <div className="feed-container">
        <h2>News Feed</h2>
        <div className="feed">
          {feedItems.map(feedItem => (
            <div key={feedItem.id} className="feed-item">
              {/* Render the feed item content */}
              <h3>{feedItem.username}</h3>
              <p>{feedItem.tweet}</p>
              {/* Render the feed item image */}
              {feedItem.image && <img src={feedItem.image} alt="Feed Item" />}
            </div>
          ))}
        </div>
      </div>

     
    </CenteredContainer>
  </div>
);
};
export default Home;
