import React from 'react';
import axios from 'axios';
//import {Avatar, Button} from "@material-ui/core";
import './Home.css';

const CenteredContainer = ({ children }) => {
    return (
      <div className="centered-container">
        {children}
      </div>
    );
  }
const Home = () => {
  const [tweet, setTweet] = React.useState('');
  //const [image, setImage] = React.useState(null);

  const handleTweetChange = (e) => {
    setTweet(e.target.value);
  };

  // const handleImageChange = (e) => {
  //   setImage(e.target.files[0]);
  // };



  
  const handleSubmit = async (e) => {
    e.preventDefault();
  
    const tweetData = {
      tweet:tweet
     
    };

    axios.defaults.headers.post['Content-Type'] = 'application/json';

    axios.post("http://localhost:5000/home", tweetData)
      .then(response => {
        console.log(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

return (
  <div className='Home'>
    <h1>Welcome to the Home Page</h1>
    <CenteredContainer className='centered-container'>
      <div>
        <h2>Post a Tweet</h2>
        <form onSubmit={handleSubmit}>
          <div>
            <textarea className='tweettext' 
              id="tweet"
              value={tweet}
              onChange={handleTweetChange}
               
              placeholder="What's happening?"
              rows={5}
              cols={100}
            />
          </div>
          <button className='tweetbutton' type="submit">Tweet</button>
        </form>
      </div>
    </CenteredContainer>




  <div className="feed-container">
      <CenteredContainer className='feedcentered-container'>
        <h2>News Feed</h2>
        <div className="feed-scroll-container">
          <div className="feed">
            {/* Placeholder content for feed items */}
            <div className="feed-item">
              <h3>Username</h3>
              <p>Sample tweet content</p>
            </div>
            <div className="feed-item">
              <h3>Another User</h3>
              <p>Another tweet</p>
            </div>
          </div>
        </div>
      </CenteredContainer>

  </div>
</div>

   
);
};
export default Home;
